(79, 1, '2015-07-05 19:06:03', '2015-07-05 19:06:03', 'The following is the complete source code for the basic Winsock TCP/IP Client Application.\r\n<pre class="lang:default decode:true " title="WinSock TCP/IP Client Program">/**\r\n* Author: Rajib Chandra Das\r\n* Topic: WinSock TCP/IP Client Program\r\n* Date: 5_July_2015\r\n*/\r\n\r\n#include &lt;winsock2.h&gt;\r\n#include &lt;ws2tcpip.h&gt;\r\n#include &lt;windows.h&gt;\r\n#include &lt;stdlib.h&gt;\r\n#include &lt;stdio.h&gt;\r\n#include &lt;sstream&gt;\r\n#include &lt;string&gt;\r\n\r\n// Need to link with Ws2_32.lib, Mswsock.lib, and Advapi32.lib\r\n#pragma comment (lib, "Ws2_32.lib")\r\n#pragma comment (lib, "Mswsock.lib")\r\n#pragma comment (lib, "AdvApi32.lib")\r\n\r\n\r\n#define DEFAULT_BUFLEN 1024\r\n#define DEFAULT_PORT "12345"\r\n\r\nusing namespace std;\r\n\r\nint main(int argc, char **argv)\r\n{\r\n	WSADATA wsaData;\r\n	SOCKET ConnectSocket = INVALID_SOCKET;\r\n	struct addrinfo *result = NULL,\r\n		*ptr = NULL,\r\n		hints;\r\n	char *sendbuf = "this is a test";\r\n	char recvbuf[DEFAULT_BUFLEN];\r\n	int iResult;\r\n	int recvbuflen = DEFAULT_BUFLEN;\r\n\r\n	// Initialize Winsock\r\n	iResult = WSAStartup(MAKEWORD(2, 2), &amp;wsaData);\r\n	if (iResult != 0) {\r\n		printf("WSAStartup failed with error: %d\\n", iResult);\r\n		return 1;\r\n	}\r\n\r\n	ZeroMemory(&amp;hints, sizeof(hints));\r\n	hints.ai_family = AF_UNSPEC;\r\n	hints.ai_socktype = SOCK_STREAM;\r\n	hints.ai_protocol = IPPROTO_TCP;\r\n\r\n	// Resolve the server address and port\r\n	iResult = getaddrinfo("192.168.0.104", DEFAULT_PORT, &amp;hints, &amp;result); //Here ServerIP = 192.168.0.104, ServerPort = 12345\r\n	if (iResult != 0) {\r\n		printf("getaddrinfo failed with error: %d\\n", iResult);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	// Attempt to connect to an address until one succeeds\r\n	for (ptr = result; ptr != NULL; ptr = ptr-&gt;ai_next) {\r\n\r\n		// Create a SOCKET for connecting to server\r\n		ConnectSocket = socket(ptr-&gt;ai_family, ptr-&gt;ai_socktype,\r\n			ptr-&gt;ai_protocol);\r\n		if (ConnectSocket == INVALID_SOCKET) {\r\n			printf("socket failed with error: %ld\\n", WSAGetLastError());\r\n			WSACleanup();\r\n			return 1;\r\n		}\r\n\r\n		// Connect to server.\r\n		iResult = connect(ConnectSocket, ptr-&gt;ai_addr, (int)ptr-&gt;ai_addrlen);\r\n		if (iResult == SOCKET_ERROR) {\r\n			closesocket(ConnectSocket);\r\n			ConnectSocket = INVALID_SOCKET;\r\n			continue;\r\n		}\r\n		break;\r\n	}\r\n\r\n	freeaddrinfo(result);\r\n\r\n	if (ConnectSocket == INVALID_SOCKET) {\r\n		printf("Unable to connect to server!\\n");\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	int kounter = 0;\r\n	while (true)\r\n	{\r\n		Sleep(500);\r\n		stringstream ss;\r\n		string s = "";\r\n		ss &lt;&lt; (++kounter);\r\n		ss &gt;&gt; s;\r\n		string sMsg = "Rajib is Sending Data to Server --&gt; " + s;\r\n		printf("Sending: %s\\n", sMsg.c_str());\r\n		iResult = send(ConnectSocket, sMsg.c_str(), sMsg.size(), 0);		//Sending message to Server\r\n		if (iResult == SOCKET_ERROR) {\r\n			printf("send failed with error: %d\\n", WSAGetLastError());\r\n			closesocket(ConnectSocket);\r\n			WSACleanup();\r\n			return 1;\r\n		}\r\n\r\n		iResult = recv(ConnectSocket, recvbuf, recvbuflen, 0);				//Receiving message from Server\r\n		if (iResult &gt; 0)\r\n		{\r\n			//printf("Bytes received from Server: %d\\n", iResult);\r\n			printf("Receiving: ");\r\n			for (int i = 0; i &lt; iResult; i++)\r\n				printf("%c", recvbuf[i]);\r\n			printf("\\n");\r\n		}\r\n	}\r\n	\r\n\r\n	// shutdown the connection since no more data will be sent\r\n	iResult = shutdown(ConnectSocket, SD_SEND);\r\n	if (iResult == SOCKET_ERROR) {\r\n	printf("shutdown failed with error: %d\\n", WSAGetLastError());\r\n	closesocket(ConnectSocket);\r\n	WSACleanup();\r\n	return 1;\r\n	}\r\n	\r\n	// cleanup\r\n	closesocket(ConnectSocket);\r\n	WSACleanup();\r\n	return 0;\r\n}</pre>\r\n&nbsp;', 'Sample Client Program that connects with a TCP Server, Windows', '', 'publish', 'open', 'open', '', 'sample-client-program-that-connects-with-a-tcp-server', '', '', '2015-07-06 10:32:40', '2015-07-06 10:32:40', '', 0, 'http://rajib.me/?p=79', 0, 'post', '', 1),
