istenSocket == INVALID_SOCKET) {\r\n		printf("socket failed with error: %ld\\n", WSAGetLastError());\r\n		freeaddrinfo(result);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	cout &lt;&lt; "Rajib_Check: ListenSocket creation successfull" &lt;&lt; endl;\r\n\r\n	// Setup the TCP listening socket\r\n	iResult = bind(ListenSocket, result-&gt;ai_addr, (int)result-&gt;ai_addrlen);\r\n	if (iResult == SOCKET_ERROR) {\r\n		printf("bind failed with error: %d\\n", WSAGetLastError());\r\n		freeaddrinfo(result);\r\n		closesocket(ListenSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n	cout &lt;&lt; "Rajib_Check: Binding Listen socket to Server IP" &lt;&lt; endl;\r\n\r\n	freeaddrinfo(result);\r\n\r\n	iResult = listen(ListenSocket, SOMAXCONN);\r\n	if (iResult == SOCKET_ERROR) {\r\n		printf("listen failed with error: %d\\n", WSAGetLastError());\r\n		closesocket(ListenSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n	cout &lt;&lt; "Rajib_Check: listen successfull... waiting for a client socket" &lt;&lt; endl;\r\n\r\n	// Accept a client socket\r\n	ClientSocket = accept(ListenSocket, NULL, NULL);\r\n	if (ClientSocket == INVALID_SOCKET) {\r\n		printf("accept failed with error: %d\\n", WSAGetLastError());\r\n		closesocket(ListenSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n	cout &lt;&lt; "Rajib_Check: ClientSocket accept successfull" &lt;&lt; endl;\r\n\r\n	// No longer need server socket\r\n\r\n	//closesocket(ListenSocket);\r\n\r\n	cout &lt;&lt; "Rajib_Check: Here before starting receving looop" &lt;&lt; endl;\r\n	// Receive until the peer shuts down the connection\r\n	do {\r\n		cout &lt;&lt; "Rajib_Check: &lt;--- Server is now in RECEIVE STATE ---&gt;" &lt;&lt; endl;\r\n		iResult = recv(ClientSocket, recvbuf, recvbuflen, 0);\r\n		cout &lt;&lt; "Rajib_Check: &lt;--- Server is now in PROCESS STATE ---&gt;" &lt;&lt; endl;\r\n		if (iResult &gt; 0)\r\n		{\r\n			//printf("Bytes received: %d\\n", iResult);\r\n			printf("Server Received: ");\r\n			for (int i = 0; i &lt; iResult; i++)\r\n				printf("%c", recvbuf[i]);\r\n			printf("\\n");\r\n\r\n			// Echo the buffer back to the sender\r\n			string sMsg(recvbuf, iResult);\r\n			sMsg += ", This is ECHO from Server";\r\n			printf("Server Sending: %s\\n", sMsg.c_str());\r\n			iSendResult = send(ClientSocket, sMsg.c_str(), sMsg.size(), 0);\r\n			if (iSendResult == SOCKET_ERROR) {\r\n				printf("send failed with error: %d\\n", WSAGetLastError());\r\n				closesocket(ClientSocket);\r\n				WSACleanup();\r\n				return 1;\r\n			}\r\n			printf("Bytes sent: %d\\n", iSendResult);\r\n		}\r\n		else if (iResult == 0)\r\n			printf("Connection closing...\\n");\r\n		else\r\n		{\r\n			printf("recv failed with error: %d\\n", WSAGetLastError());\r\n			closesocket(ClientSocket);\r\n			WSACleanup();\r\n			return 1;\r\n		}\r\n		Sleep(1000);\r\n	} while (true);\r\n\r\n	// shutdown the connection since we''re done\r\n	iResult = shutdown(ClientSocket, SD_SEND);\r\n	if (iResult == SOCKET_ERROR) {\r\n		printf("shutdown failed with error: %d\\n", WSAGetLastError());\r\n		closesocket(ClientSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	// cleanup\r\n	closesocket(ClientSocket);\r\n	WSACleanup();\r\n\r\n	return 0;\r\n}</pre>\r\n&nbsp;', 'Sample Server Program that uses TCP Protocol, Windows', '', 'publish', 'open', 'open', '', 'sample-server-program-that-uses-tcp-protocol', '', '', '2015-07-06 10:32:12', '2015-07-06 10:32:12', '', 0, 'http://rajib.me/?p=81', 0, 'post', '', 1),
