(91, 1, '2015-07-06 10:32:12', '2015-07-06 10:32:12', 'The following is the complete source code for the basic Winsock TCP/IP Server application.\r\n<pre class="lang:default decode:true " title="WinSock TCP/IP Server Program">/**\r\n* Author: Rajib Chandra Das\r\n* Topic: WinSock TCP/IP Server Program\r\n* Date: 5_July_2015\r\n*/\r\n#include &lt;winsock2.h&gt;\r\n#include &lt;ws2tcpip.h&gt;\r\n#include &lt;windows.h&gt;\r\n#include &lt;stdlib.h&gt;\r\n#include &lt;stdio.h&gt;\r\n#include &lt;iostream&gt;\r\n#include &lt;string&gt;\r\n#include &lt;sstream&gt;\r\n\r\n#pragma comment (lib, "Ws2_32.lib")\r\n#pragma comment (lib, "Mswsock.lib")\r\n#pragma comment (lib, "AdvApi32.lib")\r\n\r\n#define DEFAULT_PORT  "12345"\r\n#define DEFAULT_BUFLEN 1024\r\n\r\nusing namespace std;\r\nint main()\r\n{\r\n	WSADATA wsaData;\r\n	int iResult;\r\n\r\n	SOCKET ListenSocket = INVALID_SOCKET;\r\n	SOCKET ClientSocket = INVALID_SOCKET;\r\n\r\n	struct addrinfo *result = NULL;\r\n	struct addrinfo hints;\r\n\r\n	int iSendResult;\r\n	char recvbuf[DEFAULT_BUFLEN];\r\n	int recvbuflen = DEFAULT_BUFLEN;\r\n\r\n	// Initialize Winsock\r\n	iResult = WSAStartup(MAKEWORD(2, 2), &amp;wsaData);\r\n	if (iResult != 0) {\r\n		printf("WSAStartup failed with error: %d\\n", iResult);\r\n		return 1;\r\n	}\r\n\r\n	ZeroMemory(&amp;hints, sizeof(hints));\r\n	hints.ai_family = AF_INET;\r\n	hints.ai_socktype = SOCK_STREAM;\r\n	hints.ai_protocol = IPPROTO_TCP;\r\n	hints.ai_flags = AI_PASSIVE;\r\n\r\n	// Resolve the server address and port\r\n	iResult = getaddrinfo(NULL, DEFAULT_PORT, &amp;hints, &amp;result); //Server Address will be automatically "localhost"\r\n	if (iResult != 0) {\r\n		printf("getaddrinfo failed with error: %d\\n", iResult);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	cout &lt;&lt; "Rajib_Check: getaddrinfo successfull" &lt;&lt; endl;\r\n\r\n	// Create a SOCKET for connecting to server\r\n	ListenSocket = socket(result-&gt;ai_family, result-&gt;ai_socktype, result-&gt;ai_protocol);\r\n	if (ListenSocket == INVALID_SOCKET) {\r\n		printf("socket failed with error: %ld\\n", WSAGetLastError());\r\n		freeaddrinfo(result);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	cout &lt;&lt; "Rajib_Check: ListenSocket creation successfull" &lt;&lt; endl;\r\n\r\n	// Setup the TCP listening socket\r\n	iResult = bind(ListenSocket, result-&gt;ai_addr, (int)result-&gt;ai_addrlen);\r\n	if (iResult == SOCKET_ERROR) {\r\n		printf("bind failed with error: %d\\n", WSAGetLastError());\r\n		freeaddrinfo(result);\r\n		closesocket(ListenSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n	cout &lt;&lt; "Rajib_Check: Binding Listen socket to Server IP" &lt;&lt; endl;\r\n\r\n	freeaddrinfo(result);\r\n\r\n	iResult = listen(ListenSocket, SOMAXCONN);\r\n	if (iResult == SOCKET_ERROR) {\r\n		printf("listen failed with error: %d\\n", WSAGetLastError());\r\n		closesocket(ListenSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n	cout &lt;&lt; "Rajib_Check: listen successfull... waiting for a client socket" &lt;&lt; endl;\r\n\r\n	// Accept a client socket\r\n	ClientSocket = accept(ListenSocket, NULL, NULL);\r\n	if (ClientSocket == INVALID_SOCKET) {\r\n		printf("accept failed with error: %d\\n", WSAGetLastError());\r\n		closesocket(ListenSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n	cout &lt;&lt; "Rajib_Check: ClientSocket accept successfull" &lt;&lt; endl;\r\n\r\n	// No longer need server socket\r\n\r\n	//closesocket(ListenSocket);\r\n\r\n	cout &lt;&lt; "Rajib_Check: Here before starting receving looop" &lt;&lt; endl;\r\n	// Receive until the peer shuts down the connection\r\n	do {\r\n		cout &lt;&lt; "Rajib_Check: &lt;--- Server is now in RECEIVE STATE ---&gt;" &lt;&lt; endl;\r\n		iResult = recv(ClientSocket, recvbuf, recvbuflen, 0);\r\n		cout &lt;&lt; "Rajib_Check: &lt;--- Server is now in PROCESS STATE ---&gt;" &lt;&lt; endl;\r\n		if (iResult &gt; 0)\r\n		{\r\n			//printf("Bytes received: %d\\n", iResult);\r\n			printf("Server Received: ");\r\n			for (int i = 0; i &lt; iResult; i++)\r\n				printf("%c", recvbuf[i]);\r\n			printf("\\n");\r\n\r\n			// Echo the buffer back to the sender\r\n			string sMsg(recvbuf, iResult);\r\n			sMsg += ", This is ECHO from Server";\r\n			printf("Server Sending: %s\\n", sMsg.c_str());\r\n			iSendResult = send(ClientSocket, sMsg.c_str(), sMsg.size(), 0);\r\n			if (iSendResult == SOCKET_ERROR) {\r\n				printf("send failed with error: %d\\n", WSAGetLastError());\r\n				closesocket(ClientSocket);\r\n				WSACleanup();\r\n				return 1;\r\n			}\r\n			printf("Bytes sent: %d\\n", iSendResult);\r\n		}\r\n		else if (iResult == 0)\r\n			printf("Connection closing...\\n");\r\n		else\r\n		{\r\n			printf("recv failed with error: %d\\n", WSAGetLastError());\r\n			closesocket(ClientSocket);\r\n			WSACleanup();\r\n			return 1;\r\n		}\r\n		Sleep(1000);\r\n	} while (true);\r\n\r\n	// shutdown the connection since we''re done\r\n	iResult = shutdown(ClientSocket, SD_SEND);\r\n	if (iResult == SOCKET_ERROR) {\r\n		printf("shutdown failed with error: %d\\n", WSAGetLastError());\r\n		closesocket(ClientSocket);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	// cleanup\r\n	closesocket(ClientSocket);\r\n	WSACleanup();\r\n\r\n	return 0;\r\n}</pre>\r\n&nbsp;', 'Sample Server Program that uses TCP Protocol, Windows', '', 'inherit', 'open', 'open', '', '81-revision-v1', '', '', '2015-07-06 10:32:12', '2015-07-06 10:32:12', '', 81, 'http://rajib.me/?p=91', 0, 'revision', '', 0),
(92, 1, '2015-07-06 10:32:40', '2015-07-06 10:32:40', 'The following is the complete source code for the basic Winsock TCP/IP Client Application.\r\n<pre class="lang:default decode:true " title="WinSock TCP/IP Client Program">/**\r\n* Author: Rajib Chandra Das\r\n* Topic: WinSock TCP/IP Client Program\r\n* Date: 5_July_2015\r\n*/\r\n\r\n#include &lt;winsock2.h&gt;\r\n#include &lt;ws2tcpip.h&gt;\r\n#include &lt;windows.h&gt;\r\n#include &lt;stdlib.h&gt;\r\n#include &lt;stdio.h&gt;\r\n#include &lt;sstream&gt;\r\n#include &lt;string&gt;\r\n\r\n// Need to link with Ws2_32.lib, Mswsock.lib, and Advapi32.lib\r\n#pragma comment (lib, "Ws2_32.lib")\r\n#pragma comment (lib, "Mswsock.lib")\r\n#pragma comment (lib, "AdvApi32.lib")\r\n\r\n\r\n#define DEFAULT_BUFLEN 1024\r\n#define DEFAULT_PORT "12345"\r\n\r\nusing namespace std;\r\n\r\nint main(int argc, char **argv)\r\n{\r\n	WSADATA wsaData;\r\n	SOCKET ConnectSocket = INVALID_SOCKET;\r\n	struct addrinfo *result = NULL,\r\n		*ptr = NULL,\r\n		hints;\r\n	char *sendbuf = "this is a test";\r\n	char recvbuf[DEFAULT_BUFLEN];\r\n	int iResult;\r\n	int recvbuflen = DEFAULT_BUFLEN;\r\n\r\n	// Initialize Winsock\r\n	iResult = WSAStartup(MAKEWORD(2, 2), &amp;wsaData);\r\n	if (iResult != 0) {\r\n		printf("WSAStartup failed with error: %d\\n", iResult);\r\n		return 1;\r\n	}\r\n\r\n	ZeroMemory(&amp;hints, sizeof(hints));\r\n	hints.ai_family = AF_UNSPEC;\r\n	hints.ai_socktype = SOCK_STREAM;\r\n	hints.ai_protocol = IPPROTO_TCP;\r\n\r\n	// Resolve the server address and port\r\n	iResult = getaddrinfo("192.168.0.104", DEFAULT_PORT, &amp;hints, &amp;result); //Here ServerIP = 192.168.0.104, ServerPort = 12345\r\n	if (iResult != 0) {\r\n		printf("getaddrinfo failed with error: %d\\n", iResult);\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	// Attempt to connect to an address until one succeeds\r\n	for (ptr = result; ptr != NULL; ptr = ptr-&gt;ai_next) {\r\n\r\n		// Create a SOCKET for connecting to server\r\n		ConnectSocket = socket(ptr-&gt;ai_family, ptr-&gt;ai_socktype,\r\n			ptr-&gt;ai_protocol);\r\n		if (ConnectSocket == INVALID_SOCKET) {\r\n			printf("socket failed with error: %ld\\n", WSAGetLastError());\r\n			WSACleanup();\r\n			return 1;\r\n		}\r\n\r\n		// Connect to server.\r\n		iResult = connect(ConnectSocket, ptr-&gt;ai_addr, (int)ptr-&gt;ai_addrlen);\r\n		if (iResult == SOCKET_ERROR) {\r\n			closesocket(ConnectSocket);\r\n			ConnectSocket = INVALID_SOCKET;\r\n			continue;\r\n		}\r\n		break;\r\n	}\r\n\r\n	freeaddrinfo(result);\r\n\r\n	if (ConnectSocket == INVALID_SOCKET) {\r\n		printf("Unable to connect to server!\\n");\r\n		WSACleanup();\r\n		return 1;\r\n	}\r\n\r\n	int kounter = 0;\r\n	while (true)\r\n	{\r\n		Sleep(500);\r\n		stringstream ss;\r\n		string s = "";\r\n		ss &lt;&lt; (++kounter);\r\n		ss &gt;&gt; s;\r\n		string sMsg = "Rajib is Sending Data to Server --&gt; " + s;\r\n		printf("Sending: %s\\n", sMsg.c_str());\r\n		iResult = send(ConnectSocket, sMsg.c_str(), sMsg.size(), 0);		//Sending message to Server\r\n		if (iResult == SOCKET_ERROR) {\r\n			printf("send failed with error: %d\\n", WSAGetLastError());\r\n			closesocket(ConnectSocket);\r\n			WSACleanup();\r\n			return 1;\r\n		}\r\n\r\n		iResult = recv(ConnectSocket, recvbuf, recvbuflen, 0);				//Receiving message from Server\r\n		if (iResult &gt; 0)\r\n		{\r\n			//printf("Bytes received from Server: %d\\n", iResult);\r\n			printf("Receiving: ");\r\n			for (int i = 0; i &lt; iResult; i++)\r\n				printf("%c", recvbuf[i]);\r\n			printf("\\n");\r\n		}\r\n	}\r\n	\r\n\r\n	// shutdown the connection since no more data will be sent\r\n	iResult = shutdown(ConnectSocket, SD_SEND);\r\n	if (iResult == SOCKET_ERROR) {\r\n	printf("shutdown failed with error: %d\\n", WSAGetLastError());\r\n	closesocket(ConnectSocket);\r\n	WSACleanup();\r\n	return 1;\r\n	}\r\n	\r\n	// cleanup\r\n	closesocket(ConnectSocket);\r\n	WSACleanup();\r\n	return 0;\r\n}</pre>\r\n&nbsp;', 'Sample Client Program that connects with a TCP Server, Windows', '', 'inherit', 'open', 'open', '', '79-revision-v1', '', '', '2015-07-06 10:32:40', '2015-07-06 10:32:40', '', 79, 'http://rajib.me/?p=92', 0, 'revision', '', 0),
(93, 1, '2015-07-07 19:52:06', '2015-07-07 19:52:06', 'Here is shared complete working example of Registry Key processor using C++.\r\n<pre class="wrap:true scroll:true lang:default decode:true " title="Windows Registry Key Processor">/**\r\n* Author: Rajib Chandra Das\r\n* Topic: Windows Registry Key Processor(Create, Insert, Read, Update and Delete)\r\n* Date: 8_July_2015\r\n*/\r\n\r\n\r\n//Create C++ Win32 Project in Visual Studio\r\n//Project -&gt; "project" Properties-&gt;Configuration Properties-&gt;C/C++-&gt;Advanced-&gt;Show Includes : YES(/ showIncludes)\r\n//Project -&gt; "project" Properties-&gt;Configuration Properties-&gt;General-&gt;Project Defaults-&gt;Use of MFC : Use MFC in a shared DLL\r\n\r\n#include &lt;iostream&gt;\r\n#include &lt;afx.h&gt;\r\nusing namespace std;\r\n\r\nint CreateRegistryKey(CString ConstantKeyPath);\r\nint ReadRegistryKeyAttributes(CString ConstantKeyPath);\r\nint UpdateRegistryKeyAttribute(CString ConstantKeyPath, CString Attribute, DWORD value);\r\nint DeleteRegistryKey(CString ConstantKeyPath);\r\nint main()\r\n{\r\n	int iResult;\r\n	CString KeyPath = _T("Software\\\\RCD_Technologies\\\\Rajib_Test");\r\n	\r\n	iResult =  CreateRegistryKey(KeyPath);\r\n	if (iResult&lt;0)\r\n	{\r\n		cout &lt;&lt; "CreateRegistryKey Operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "&lt;-- CreateRegistryKey Operation Successfull --&gt;" &lt;&lt; endl;\r\n\r\n	iResult = ReadRegistryKeyAttributes(KeyPath);\r\n	if (iResult &lt; 0)\r\n	{\r\n		cout &lt;&lt; "ReadRegistryKeyAttributes operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "&lt;--- ReadRegistryKeyAttribute Operation Successfull --&gt;" &lt;&lt; endl;\r\n	\r\n\r\n\r\n	iResult = UpdateRegistryKeyAttribute(KeyPath, _T("VersionID"), (DWORD)456);\r\n	if (iResult &lt; 0)\r\n	{\r\n		cout &lt;&lt; "UpdateRegistryKeyAttribute operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "&lt;--- UpdateRegistryKeyAttribute Operation Successfull --&gt;" &lt;&lt; endl;\r\n	\r\n	\r\n\r\n	/*\r\n	iResult = DeleteRegistryKey(KeyPath);  //This Function is working fine, Skipped in Execution to keep the Registry Key\r\n	if (iResult &lt; 0)\r\n	{\r\n		cout &lt;&lt; "DeleteRegistryKey Operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "DeleteRegistryKey Operation Successfull" &lt;&lt; endl;\r\n	*/\r\n\r\n	getchar();\r\n\r\n	return 0;\r\n}\r\n\r\n/*\r\n*Opeing RegistryKey by given KeyPath\r\n*Update Attirbute by given Value and Key Name\r\n*/\r\nint UpdateRegistryKeyAttribute(CString ConstantKeyPath, CString Attribute, DWORD value)\r\n{\r\n	HKEY MyRegistryKey;\r\n	if (RegOpenKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, KEY_ALL_ACCESS, &amp;MyRegistryKey) != ERROR_SUCCESS)\r\n	{\r\n		cout &lt;&lt; "KeyOpen Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n\r\n	if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, Attribute, 0, REG_DWORD, (const BYTE*)&amp;value, sizeof(value)))\r\n	{\r\n		//m_pMessageBox-&gt;ShowMessageBox("ConstantKey Start Initialization Failed");\r\n		cout &lt;&lt; "Attribute Initialization Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n\r\n	cout &lt;&lt; "Attribute Updated with value " &lt;&lt; value &lt;&lt; endl;\r\n	return 1; //Successfull\r\n\r\n}\r\n\r\n/*\r\n*Opeing RegistryKey by given KeyPath\r\n*Reading Attirbute: VersionID, Expired, Registered\r\n*/\r\nint ReadRegistryKeyAttributes(CString ConstantKeyPath)\r\n{\r\n	HKEY MyRegistryKey;\r\n	if (RegOpenKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, KEY_READ, &amp;MyRegistryKey) != ERROR_SUCCESS)\r\n	{\r\n		cout &lt;&lt; "KeyOpen Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n\r\n\r\n	DWORD type = REG_DWORD;\r\n	DWORD cbData;\r\n	unsigned long size = 1024;\r\n	CString csVersionID;\r\n\r\n	csVersionID = _T("VersionID");\r\n	if (RegQueryValueEx(MyRegistryKey, csVersionID, NULL, &amp;type, (LPBYTE)&amp;cbData, &amp;size) != ERROR_SUCCESS)\r\n	{\r\n		RegCloseKey(MyRegistryKey);\r\n		cout &lt;&lt; "VersionID Key Attribute Reading Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n	else\r\n	{\r\n		cout &lt;&lt; "VersionID = " &lt;&lt; cbData &lt;&lt; endl;\r\n	}\r\n\r\n	csVersionID = _T("Expired");\r\n	if (RegQueryValueEx(MyRegistryKey, csVersionID, NULL, &amp;type, (LPBYTE)&amp;cbData, &amp;size) != ERROR_SUCCESS)\r\n	{\r\n		RegCloseKey(MyRegistryKey);\r\n		cout &lt;&lt; "Expired Key Attribute Reading Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n	else\r\n	{\r\n		cout &lt;&lt; "Expired = " &lt;&lt; cbData &lt;&lt; endl;\r\n	}\r\n\r\n	csVersionID = _T("Registered");\r\n	if (RegQueryValueEx(MyRegistryKey, csVersionID, NULL, &amp;type, (LPBYTE)&amp;cbData, &amp;size) != ERROR_SUCCESS)\r\n	{\r\n		RegCloseKey(MyRegistryKey);\r\n		cout &lt;&lt; "Registered Key Attribute Reading Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n	else\r\n	{\r\n		cout &lt;&lt; "Registered = " &lt;&lt; cbData &lt;&lt; endl;\r\n	}\r\n\r\n	return 1; //Successfull\r\n}\r\n\r\n/*\r\n*Creating New Registry Key\r\n*Creating Attirbute: VersionID, Expired, Registered\r\n*/\r\nint CreateRegistryKey(CString ConstantKeyPath)\r\n{\r\n	HKEY MyRegistryKey;\r\n	DWORD d_VersionId, d_isExpired, d_isRegistered;\r\n\r\n	if (ERROR_SUCCESS != RegOpenKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, KEY_ALL_ACCESS, &amp;MyRegistryKey))\r\n	{\r\n		// Registry key by specified name not found.... so new Registry key has to be created\r\n\r\n		DWORD dwDisposition = 0;\r\n		if (ERROR_SUCCESS != RegCreateKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, NULL, 0, KEY_ALL_ACCESS, NULL, &amp;MyRegistryKey, &amp;dwDisposition))\r\n		{\r\n			cout &lt;&lt; "MyRegistryKey Creation Failed" &lt;&lt; endl;\r\n			return -1; //Error\r\n		}\r\n		else\r\n		{\r\n			d_VersionId = 20150606; //Just a Random Value considred as version ID\r\n			if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, TEXT("VersionID"), 0, REG_DWORD, (const BYTE*)&amp;d_VersionId, sizeof(d_VersionId)))\r\n			{\r\n				//m_pMessageBox-&gt;ShowMessageBox("ConstantKey Start Initialization Failed");\r\n				cout &lt;&lt; "VersionID Attribute Initialization Faield" &lt;&lt; endl;\r\n				return -1; //Error\r\n			}\r\n\r\n			d_isExpired = 0;\r\n			if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, TEXT("Expired"), 0, REG_DWORD, (const BYTE*)&amp;d_isExpired, sizeof(d_isExpired)))\r\n			{\r\n				cout &lt;&lt; "Expired Attribute Initialization Failed" &lt;&lt; endl;\r\n				return -1; //Error\r\n			}\r\n\r\n			d_isRegistered = 0;\r\n			if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, TEXT("Registered"), 0, REG_DWORD, (const BYTE*)&amp;d_isRegistered, sizeof(d_isRegistered)))\r\n			{\r\n				cout &lt;&lt; "Registered Attribute Initialization Failed" &lt;&lt; endl;\r\n				return -1; //Error\r\n			}\r\n\r\n		}\r\n\r\n	}\r\n\r\n	cout &lt;&lt; "MyRegKey Created Successfully" &lt;&lt; endl;\r\n	cout&lt;&lt;"Default Key Attributes are-- &gt; VersionID, Expired and Registered" &lt;&lt; endl;\r\n	RegCloseKey(MyRegistryKey);\r\n	return 1; //Successfull\r\n\r\n}\r\n\r\nint DeleteRegistryKey(CString ConstantKeyPath)\r\n{\r\n	int lReturn = RegDeleteKey(HKEY_CURRENT_USER, ConstantKeyPath);\r\n	return lReturn;\r\n}\r\n</pre>\r\n&nbsp;', 'How to Create, Read, Update, Delete Registry Key using C++', '', 'publish', 'open', 'open', '', 'how-to-create-read-update-delete-registry-key-using-c', '', '', '2015-07-08 05:04:29', '2015-07-08 05:04:29', '', 0, 'http://rajib.me/?p=93', 0, 'post', '', 0),
