(98, 1, '2015-07-08 05:04:29', '2015-07-08 05:04:29', 'Here is shared complete working example of Registry Key processor using C++.\r\n<pre class="wrap:true scroll:true lang:default decode:true " title="Windows Registry Key Processor">/**\r\n* Author: Rajib Chandra Das\r\n* Topic: Windows Registry Key Processor(Create, Insert, Read, Update and Delete)\r\n* Date: 8_July_2015\r\n*/\r\n\r\n\r\n//Create C++ Win32 Project in Visual Studio\r\n//Project -&gt; "project" Properties-&gt;Configuration Properties-&gt;C/C++-&gt;Advanced-&gt;Show Includes : YES(/ showIncludes)\r\n//Project -&gt; "project" Properties-&gt;Configuration Properties-&gt;General-&gt;Project Defaults-&gt;Use of MFC : Use MFC in a shared DLL\r\n\r\n#include &lt;iostream&gt;\r\n#include &lt;afx.h&gt;\r\nusing namespace std;\r\n\r\nint CreateRegistryKey(CString ConstantKeyPath);\r\nint ReadRegistryKeyAttributes(CString ConstantKeyPath);\r\nint UpdateRegistryKeyAttribute(CString ConstantKeyPath, CString Attribute, DWORD value);\r\nint DeleteRegistryKey(CString ConstantKeyPath);\r\nint main()\r\n{\r\n	int iResult;\r\n	CString KeyPath = _T("Software\\\\RCD_Technologies\\\\Rajib_Test");\r\n	\r\n	iResult =  CreateRegistryKey(KeyPath);\r\n	if (iResult&lt;0)\r\n	{\r\n		cout &lt;&lt; "CreateRegistryKey Operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "&lt;-- CreateRegistryKey Operation Successfull --&gt;" &lt;&lt; endl;\r\n\r\n	iResult = ReadRegistryKeyAttributes(KeyPath);\r\n	if (iResult &lt; 0)\r\n	{\r\n		cout &lt;&lt; "ReadRegistryKeyAttributes operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "&lt;--- ReadRegistryKeyAttribute Operation Successfull --&gt;" &lt;&lt; endl;\r\n	\r\n\r\n\r\n	iResult = UpdateRegistryKeyAttribute(KeyPath, _T("VersionID"), (DWORD)456);\r\n	if (iResult &lt; 0)\r\n	{\r\n		cout &lt;&lt; "UpdateRegistryKeyAttribute operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "&lt;--- UpdateRegistryKeyAttribute Operation Successfull --&gt;" &lt;&lt; endl;\r\n	\r\n	\r\n\r\n	/*\r\n	iResult = DeleteRegistryKey(KeyPath);  //This Function is working fine, Skipped in Execution to keep the Registry Key\r\n	if (iResult &lt; 0)\r\n	{\r\n		cout &lt;&lt; "DeleteRegistryKey Operation Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n	cout &lt;&lt; "DeleteRegistryKey Operation Successfull" &lt;&lt; endl;\r\n	*/\r\n\r\n	getchar();\r\n\r\n	return 0;\r\n}\r\n\r\n/*\r\n*Opeing RegistryKey by given KeyPath\r\n*Update Attirbute by given Value and Key Name\r\n*/\r\nint UpdateRegistryKeyAttribute(CString ConstantKeyPath, CString Attribute, DWORD value)\r\n{\r\n	HKEY MyRegistryKey;\r\n	if (RegOpenKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, KEY_ALL_ACCESS, &amp;MyRegistryKey) != ERROR_SUCCESS)\r\n	{\r\n		cout &lt;&lt; "KeyOpen Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n\r\n	if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, Attribute, 0, REG_DWORD, (const BYTE*)&amp;value, sizeof(value)))\r\n	{\r\n		//m_pMessageBox-&gt;ShowMessageBox("ConstantKey Start Initialization Failed");\r\n		cout &lt;&lt; "Attribute Initialization Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n\r\n	cout &lt;&lt; "Attribute Updated with value " &lt;&lt; value &lt;&lt; endl;\r\n	return 1; //Successfull\r\n\r\n}\r\n\r\n/*\r\n*Opeing RegistryKey by given KeyPath\r\n*Reading Attirbute: VersionID, Expired, Registered\r\n*/\r\nint ReadRegistryKeyAttributes(CString ConstantKeyPath)\r\n{\r\n	HKEY MyRegistryKey;\r\n	if (RegOpenKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, KEY_READ, &amp;MyRegistryKey) != ERROR_SUCCESS)\r\n	{\r\n		cout &lt;&lt; "KeyOpen Failed" &lt;&lt; endl;\r\n		return -1;\r\n	}\r\n\r\n\r\n	DWORD type = REG_DWORD;\r\n	DWORD cbData;\r\n	unsigned long size = 1024;\r\n	CString csVersionID;\r\n\r\n	csVersionID = _T("VersionID");\r\n	if (RegQueryValueEx(MyRegistryKey, csVersionID, NULL, &amp;type, (LPBYTE)&amp;cbData, &amp;size) != ERROR_SUCCESS)\r\n	{\r\n		RegCloseKey(MyRegistryKey);\r\n		cout &lt;&lt; "VersionID Key Attribute Reading Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n	else\r\n	{\r\n		cout &lt;&lt; "VersionID = " &lt;&lt; cbData &lt;&lt; endl;\r\n	}\r\n\r\n	csVersionID = _T("Expired");\r\n	if (RegQueryValueEx(MyRegistryKey, csVersionID, NULL, &amp;type, (LPBYTE)&amp;cbData, &amp;size) != ERROR_SUCCESS)\r\n	{\r\n		RegCloseKey(MyRegistryKey);\r\n		cout &lt;&lt; "Expired Key Attribute Reading Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n	else\r\n	{\r\n		cout &lt;&lt; "Expired = " &lt;&lt; cbData &lt;&lt; endl;\r\n	}\r\n\r\n	csVersionID = _T("Registered");\r\n	if (RegQueryValueEx(MyRegistryKey, csVersionID, NULL, &amp;type, (LPBYTE)&amp;cbData, &amp;size) != ERROR_SUCCESS)\r\n	{\r\n		RegCloseKey(MyRegistryKey);\r\n		cout &lt;&lt; "Registered Key Attribute Reading Failed" &lt;&lt; endl;\r\n		return -1; //Error\r\n	}\r\n	else\r\n	{\r\n		cout &lt;&lt; "Registered = " &lt;&lt; cbData &lt;&lt; endl;\r\n	}\r\n\r\n	return 1; //Successfull\r\n}\r\n\r\n/*\r\n*Creating New Registry Key\r\n*Creating Attirbute: VersionID, Expired, Registered\r\n*/\r\nint CreateRegistryKey(CString ConstantKeyPath)\r\n{\r\n	HKEY MyRegistryKey;\r\n	DWORD d_VersionId, d_isExpired, d_isRegistered;\r\n\r\n	if (ERROR_SUCCESS != RegOpenKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, KEY_ALL_ACCESS, &amp;MyRegistryKey))\r\n	{\r\n		// Registry key by specified name not found.... so new Registry key has to be created\r\n\r\n		DWORD dwDisposition = 0;\r\n		if (ERROR_SUCCESS != RegCreateKeyEx(HKEY_CURRENT_USER, ConstantKeyPath, 0, NULL, 0, KEY_ALL_ACCESS, NULL, &amp;MyRegistryKey, &amp;dwDisposition))\r\n		{\r\n			cout &lt;&lt; "MyRegistryKey Creation Failed" &lt;&lt; endl;\r\n			return -1; //Error\r\n		}\r\n		else\r\n		{\r\n			d_VersionId = 20150606; //Just a Random Value considred as version ID\r\n			if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, TEXT("VersionID"), 0, REG_DWORD, (const BYTE*)&amp;d_VersionId, sizeof(d_VersionId)))\r\n			{\r\n				//m_pMessageBox-&gt;ShowMessageBox("ConstantKey Start Initialization Failed");\r\n				cout &lt;&lt; "VersionID Attribute Initialization Faield" &lt;&lt; endl;\r\n				return -1; //Error\r\n			}\r\n\r\n			d_isExpired = 0;\r\n			if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, TEXT("Expired"), 0, REG_DWORD, (const BYTE*)&amp;d_isExpired, sizeof(d_isExpired)))\r\n			{\r\n				cout &lt;&lt; "Expired Attribute Initialization Failed" &lt;&lt; endl;\r\n				return -1; //Error\r\n			}\r\n\r\n			d_isRegistered = 0;\r\n			if (ERROR_SUCCESS != RegSetValueEx(MyRegistryKey, TEXT("Registered"), 0, REG_DWORD, (const BYTE*)&amp;d_isRegistered, sizeof(d_isRegistered)))\r\n			{\r\n				cout &lt;&lt; "Registered Attribute Initialization Failed" &lt;&lt; endl;\r\n				return -1; //Error\r\n			}\r\n\r\n		}\r\n\r\n	}\r\n\r\n	cout &lt;&lt; "MyRegKey Created Successfully" &lt;&lt; endl;\r\n	cout&lt;&lt;"Default Key Attributes are-- &gt; VersionID, Expired and Registered" &lt;&lt; endl;\r\n	RegCloseKey(MyRegistryKey);\r\n	return 1; //Successfull\r\n\r\n}\r\n\r\nint DeleteRegistryKey(CString ConstantKeyPath)\r\n{\r\n	int lReturn = RegDeleteKey(HKEY_CURRENT_USER, ConstantKeyPath);\r\n	return lReturn;\r\n}\r\n</pre>\r\n&nbsp;', 'How to Create, Read, Update, Delete Registry Key using C++', '', 'inherit', 'open', 'open', '', '93-revision-v1', '', '', '2015-07-08 05:04:29', '2015-07-08 05:04:29', '', 93, 'http://rajib.me/?p=98', 0, 'revision', '', 0);
